using UnityEngine; public class PlayerController:MonoBehaviour{ public float moveSpeed=5f; public float jumpForce=5f; private Rigidbody rb; void Start(){ rb=gameObject.AddComponent<Rigidbody>();} void Update(){ float h=Input.GetAxis("Horizontal"); float v=Input.GetAxis("Vertical"); Vector3 move=new Vector3(h,0,v)*moveSpeed*Time.deltaTime; transform.Translate(move); if(Input.GetKeyDown(KeyCode.Space)) rb.AddForce(Vector3.up*jumpForce,ForceMode.Impulse);}}