using UnityEngine; [System.Serializable] public class TierReward{ public int tierNumber; public SkinManager SkinReward; public int soulsReward; public bool isPremium;} public class BattlePassManager:MonoBehaviour{ public TierReward[] tiers; public int currentTier; public bool premiumPassUnlocked; public void ProgressTier(){currentTier++; CheckReward();} void CheckReward(){foreach(var reward in tiers){if(reward.tierNumber==currentTier && (!reward.isPremium||premiumPassUnlocked)){if(reward.SkinReward!=null)reward.SkinReward.UnlockSkin(reward.SkinReward.equippedSkinId); if(reward.soulsReward>0)SoulManager.Instance.AddSouls(reward.soulsReward);}}} public void UnlockPremiumPass(){if(SoulManager.Instance.SpendSouls(1000)) premiumPassUnlocked=true;}}