using UnityEngine; using UnityEngine.UI; public class IAPManager:MonoBehaviour{public static IAPManager Instance; public int dummyBalance=0; public Text balanceText; void Awake()=>Instance=this; void Start()=>UpdateBalanceUI(); public void BuySouls(int amount){dummyBalance+=amount; UpdateBalanceUI(); Debug.Log($"Purchased {amount} souls (dummy). Current balance: {dummyBalance}");} void UpdateBalanceUI(){if(balanceText) balanceText.text=$"Souls: {dummyBalance}";}}