using UnityEngine; using System.Threading.Tasks; using Unity.Services.Leaderboards; using Unity.Services.Core; using Unity.Services.Authentication; public class LeaderboardManager:MonoBehaviour{ async void Start(){ await UnityServices.InitializeAsync(); if(!AuthenticationService.Instance.IsSignedIn) await AuthenticationService.Instance.SignInAnonymouslyAsync(); } public async Task SubmitScore(string leaderboardId,long score){ await LeaderboardsService.Instance.AddPlayerScoreAsync(leaderboardId,score);}}