using UnityEngine; [System.Serializable] public class SkinData{public string skinId; public string displayName; public int costSouls; public string iapProductId; public Sprite icon; public GameObject modelPrefab;} public class SkinManager:MonoBehaviour{public SkinData[] allSkins; public string equippedSkinId; void Start(){LoadEquippedSkin();} public void UnlockSkin(string skinId){PlayerPrefs.SetInt("skin_"+skinId,1);} public bool IsUnlocked(string skinId){return PlayerPrefs.GetInt("skin_"+skinId,0)==1;} public void EquipSkin(string skinId){if(IsUnlocked(skinId)){equippedSkinId=skinId; PlayerPrefs.SetString("equippedSkin",skinId);}} void LoadEquippedSkin(){equippedSkinId=PlayerPrefs.GetString("equippedSkin",allSkins[0].skinId); EquipSkin(equippedSkinId);}}