using UnityEngine; using UnityEngine.UI; public class SoulManager : MonoBehaviour { public static SoulManager Instance; public int currentSouls=0; public Text soulText; void Awake()=>Instance=this; void Start()=>UpdateSoulUI(); public void AddSouls(int amount){currentSouls+=amount;UpdateSoulUI();} public bool SpendSouls(int amount){if(currentSouls>=amount){currentSouls-=amount;UpdateSoulUI();return true;}return false;} void UpdateSoulUI(){if(soulText)soulText.text=$"Souls: {currentSouls}";}}